//TypeScriptApi for JSR


declare enum EventKey {
    /**
     * 无作用
     */
    Nothing = "Nothing",
    /**
     * onServerCmd - 后台指令监听
     */
    onServerCmd = "onServerCmd",
    /**
     * onServerCmdOutput - 后台指令输出信息监听
     */
    onServerCmdOutput = "onServerCmdOutput",
    /**
     * onFormSelect - 玩家选择GUI表单监听
     */
    onFormSelect = "onFormSelect",
    /**
     * onUseItem - 使用物品监听
     */
    onUseItem = "onUseItem",
    /**
     * onPlacedBlock - 放置方块监听
     */
    onPlacedBlock = "onPlacedBlock",
    /**
     * onDestroyBlock - 破坏方块监听
     */
    onDestroyBlock = "onDestroyBlock",
    /**
     * onStartOpenChest - 开箱监听
     */
    onStartOpenChest = "onStartOpenChest",
    /**
     * onStartOpenBarrel - 开桶监听
     */
    onStartOpenBarrel = "onStartOpenBarrel",
    /**
     * onStopOpenChest - 关箱监听
     */
    onStopOpenChest = "onStopOpenChest",
    /**
     * onStopOpenBarrel - 关桶监听
     */
    onStopOpenBarrel = "onStopOpenBarrel",
    /**
     * onSetSlot - 放入取出物品监听
     */
    onSetSlot = "onSetSlot",
    /**
     * onChangeDimension - 切换维度监听
     */
    onChangeDimension = "onChangeDimension",
    /**
     * onMobDie - 生物死亡监听
     */
    onMobDie = "onMobDie",
    /**
     * onMobHurt - 生物受伤监听
     */
    onMobHurt = "onMobHurt",
    /**
     * onRespawn - 玩家重生监听
     */
    onRespawn = "onRespawn",
    /**
     * onChat - 聊天监听
     */
    onChat = "onChat",
    /**
     * onInputText - 玩家输入文本监听
     */
    onInputText = "onInputText",
    /**
     * onCommandBlockUpdate - 玩家更新命令方块监听
     */
    onCommandBlockUpdate = "onCommandBlockUpdate",
    /**
     * onInputCommand - 玩家输入指令监听
     */
    onInputCommand = "onInputCommand",
    /**
     * onBlockCmd - 命令方块(矿车)执行指令监听
     */
    onBlockCmd = "onBlockCmd",
    /**
     * onNpcCmd - NPC执行指令监听
     */
    onNpcCmd = "onNpcCmd",
    /**
     * onLoadName -  加载名字监听
     */
    onLoadName = "onLoadName",
    /**
     * onPlayerLeft - 离开游戏监听
     */
    onPlayerLeft = "onPlayerLeft",
    /**
     * onMove - 移动监听
     */
    onMove = "onMove",
    /**
     * onAttack - 攻击监听
     */
    onAttack = "onAttack",
    /**
     * onLevelExplode - 爆炸监听
     */
    onLevelExplode = "onLevelExplode",
    /**
     * onEquippedArmor - 玩家切换护甲监听
     */
    onEquippedArmor = "onEquippedArmor",
    /**
     * onLevelUp - 玩家升级
     */
    onLevelUp = "onLevelUp",
    /**
     * onPistonPush - 活塞推方块事件
     */
    onPistonPush = "onPistonPush",
    /**
     * onChestPair - 箱子合并事件
     */
    onChestPair = "onChestPair",
    /**
     * onMobSpawnCheck - 生物生成检查事件
     */
    onMobSpawnCheck = "onMobSpawnCheck",
    /**
     * onDropItem - 玩家丢物品事件
     */
    onDropItem = "onDropItem",
    /**
     * onPickUpItem - 玩家捡起物品事件
     */
    onPickUpItem = "onPickUpItem",
    /**
     * onScoreChanged - 计分板数值改变事件
     */
    onScoreChanged = "onScoreChanged",
    /**
     * onScriptEngineInit - 官方脚本引擎初始化
     */
    onScriptEngineInit = "onScriptEngineInit",
    /**
     * onScriptEngineLog - 官方脚本接收到日志
     */
    onScriptEngineLog = "onScriptEngineLog",
    /**
     * onScriptEngineCmd - 官方脚本引擎执行指令
     */
    onScriptEngineCmd = "onScriptEngineCmd",
    /**
     * onScoreboardInit - 系统计分板初始化
     */
    onScoreboardInit = "onScoreboardInit"
}

/**
 * 功能:注册玩家事件加载前监听器
 * 参数个数：2个
 * @param key 注册用关键字
 * @param func 供事件触发时的回调函数对象
 */
declare function addBeforeActListener(key: EventKey | string, func: ((eventArgsStr: string) => boolean | void))
/**
 * 功能:移除玩家事件监听器
 * 参数个数：1个
 * @param key 注册用关键字
 */
declare function removeBeforeActListener(key: EventKey | string)
/**
 * 注意:该重载仅在PFJSR中可用
 * 功能:移除玩家事件监听器
 * 参数个数：1个
 * @param key 注册用关键字
 */
declare function removeBeforeActListener(key: EventKey | string, func: ((eventArgsStr: string) => boolean | void))
/**
 * 功能:注册玩家事件加载后监听器
 * 参数个数：2个
 * @param key 注册用关键字
 * @param func 供事件触发时的回调函数对象
 */
declare function addAfterActListener(key: EventKey | string, func: ((eventArgsStr: string) => boolean | void))
/**
 * 功能:移除玩家事件加载后监听器
 * 参数个数：1个
 * @param key 注册用关键字
 */
declare function removeAfterActListener(key: EventKey | string)
/**
 * 注意:该重载仅在PFJSR中可用
 * 功能:移除玩家事件加载后监听器
 * 参数个数：1个
 * @param key 注册用关键字
 */
declare function removeAfterActListener(key: EventKey | string, func: ((eventArgsStr: string) => boolean | void))


